CREATE TABLE users ( id SERIAL PRIMARY KEY, email VARCHAR(255) UNIQUE NOT NULL, password TEXT NOT NULL, first_name VARCHAR(100) NOT NULL, last_name VARCHAR(100) NOT NULL, birth_date DATE, address TEXT, created_at TIMESTAMP DEFAULT NOW() ); CREATE TABLE agencies ( id SERIAL PRIMARY KEY, name VARCHAR(255) NOT NULL, city VARCHAR(100) NOT NULL, country VARCHAR(100) NOT NULL ); CREATE TABLE rentals ( id SERIAL PRIMARY KEY, user_id INTEGER REFERENCES users(id), start_agency_id INTEGER REFERENCES agencies(id), end_agency_id INTEGER REFERENCES agencies(id), start_datetime TIMESTAMP NOT NULL, end_datetime TIMESTAMP NOT NULL, vehicle_category VARCHAR(10) NOT NULL, price NUMERIC(10,2) NOT NULL ); CREATE TABLE payments ( id SERIAL PRIMARY KEY, rental_id INTEGER REFERENCES rentals(id), provider VARCHAR(100), status VARCHAR(50), amount NUMERIC(10,2), created_at TIMESTAMP DEFAULT NOW() ); CREATE TABLE support_messages ( id SERIAL PRIMARY KEY, user_id INTEGER REFERENCES users(id), content TEXT NOT NULL, created_at TIMESTAMP DEFAULT NOW() );